    useEffect(() => {
        const interval = setInterval(() => {
            setSeconds(seconds => seconds + 1);
            }, 1000);
        return () => clearInterval(interval);
    }, 
        []
    );
    useEffect(() =>{
        const minutInterval = setminutinterval(() =>{
            setMinuts(minuts => minuts + 1);
        }, 60000);
            return() => clearInterval(minutInterval)
    },
        []
    );
    return (
        <button>{seconds}</button>
    );
};
